mapper.select:
SELECT *
FROM AUCTIONITEM
WHERE USERID = #{userId}
AND ITEMID
= #{itemId}

select myPrice
from auctionItem
where itemId = #{itemId}

mapper.update:
update auctionItem
set
myPrice = {#price}
where userId={#userId} and
itemId={#itemId}

mapper.delete:
DELETE
FROM ACCOUNT
WHERE USERID = #{param1} and itemId = #{param2}

mapper.update:
UPDATE auctionItem
SET
state = #{state}
where userId = #{userId} and itemId = #{itemId}
